import React from 'react';
import { StyleSheet, Text, View, Image } from 'react-native';
import {Picker} from '@react-native-picker/picker';

export default class HomeScreen extends React.Component {
  render() {
    const pressHandler = () => {
      navigation.goBack();
    }
    return (
      <View style={styles.container}>
        <Image source={require('../assets/SabespLogo.png')} style={styles.logo}/>
        <Picker
          selectedValue='selecione o sistema'
          style={{height:30, width: 150, alignSelf:'center'}}
          onValueChange={(itemValue, itemIndex) =>
            this.setState({sistema: itemValue})
          }>
          <Picker.Item label="selecione o sistema" value="selecione o sistema"/>
          <Picker.Item label="Cantareira" value="Cantareira"/>
          <Picker.Item label="Guarapiranga" value="Guarapiranga"/>
        </Picker>
        <Picker
          selectedValue='selecione o periodo'
          style={{height:30, width: 150, alignItems: 'Picker'}}
          onValueChange={(itemValue, itemIndex) =>
            this.setState({periodo: itemValue})
          }>
          <Picker.Item label="selecione o periodo" value="selecione o sistema"/>
          <Picker.Item label="setembro" value="setembro"/>
          <Picker.Item label="outubro" value="outubro"/>
        </Picker>
      </View>
    );
  };
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'left',
  },
  logo: {
    marginTop: 30,
    marginBottom: 30,
    width: '10%',
    height: '10%',
    alignSelf: 'left'

  },
});
