import React, {useState} from 'react';
import { StyleSheet, Text, View, Image } from 'react-native';
import {Picker} from '@react-native-picker/picker';

export default class HomeScreen extends React.Component {
  render() {
    const pressHandler = () => {
      navigation.goBack();
    }

    const [sistema, setSistema] = useState(['Cantareira', 'Guarapiranga'])
    const [sistemaSelecionado, setSistemaSelecionado] = useState([])
    const [periodo, setPeriodo] = useState(['setembro', 'outubro'])
    const [periodoSelecionado, setPeriodoSelecionado] = useState([])
    return (
      <View style={styles.container}>
        <Image source={require('../assets/SabespLogo.png')} style={styles.logo}/>
        <Picker
          selectedValue={sistemaSelecionado}
          style={{height:30, width: 150, alignSelf:'center'}}
          onValueChange={(itemValue, itemIndex) =>
            setSistemaSelecionado(itemValue)
          }>
            {
              sistema.map(cr => {
                return <Picker.Item label={cr} value={cr}/>
              })
            }
        </Picker>
        <Picker
          selectedValue={periodoSelecionado}
          style={{height:30, width: 150, alignSelf:'center'}}
          onValueChange={(itemValue, itemIndex) =>
            setPeriodoSelecionado(itemValue)
          }>
            {
              periodo.map(cr => {
                return <Picker.Item label={cr} value={cr}/>
              })
            }
        </Picker>
      </View>
    );
  };
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'left',
  },
  logo: {
    marginTop: 30,
    marginBottom: 30,
    width: '10%',
    height: '10%',
    alignSelf: 'left'

  },
});
