import React, {useState} from 'react';
import { StyleSheet, Text, View, Image } from 'react-native';
import {Picker} from '@react-native-picker/picker';

export default function Report({ navigation }) {

    const [sistema, setSistema] = useState(['Selecione um sistema', 'Guarapiranga', 'Cantareira'])
    const [sistemaSelecionado, setsistemaSelecionado] = useState([])

    const pressHandler = () => {
      navigation.goBack();
    }

    return (
      <View style={styles.container}>
        <Image source={require('../assets/SabespLogo.png')} style={styles.logo}/>
        <Picker
          selectedValue={sistemaSelecionado}
          style={{height:30, width: 150, alignSelf:'center'}}
          onValueChange={(itemValue) =>
            setsistemaSelecionado({itemValue})
          }>
            {
              sistema.map(cr =>{
                return <Picker.Item label={cr} value={cr} />
              })
            }
        </Picker>
        <Picker
          selectedValue='selecione o periodo'
          style={{height:30, width: 150, alignSelf:'center'}}
          onValueChange={(itemValue, itemIndex) =>
            this.setState({periodo: itemValue})
          }>
          <Picker.Item label="selecione o periodo" value="selecione o sistema"/>
          <Picker.Item label="setembro" value="setembro"/>
          <Picker.Item label="outubro" value="outubro"/>
        </Picker>
      </View>
    );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'left',
  },
  logo: {
    marginTop: 30,
    marginBottom: 30,
    width: '10%',
    height: '10%',
    alignSelf: 'left'

  },
});
